// 수정대체원가그래프(전체)
scwin.fnSelectAllHdqrAsetWrthPrssListChart = function(){
	var wsAdapter = {};
	wsAdapter['ref'] = "data:dltAllHdqrAsetWrthPrssList";
	wsAdapter['seriesColumns'] = "[['awevAmt','mgmtGoal']]";
	wsAdapter['labelNode'] = "clss";
	var series = getWebSquareSeries(wsAdapter);


	var chartId = 'allHdqrAsetWrthPrssListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.ref="data:dltAllHdqrAsetWrthPrssList";
	option.seriesColumns="[['awevAmt','mgmtGoal']]";
	option.labelNode="Month";
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '원';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(본부)';
	option.unitLabel_Verti			= '금액(억원)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('수정대체원가','bar',series[0]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries1.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries1.s_sizeDefault = 1;//점의 크기
	aSeries1.s_shape='circle';//점의 모양
	aSeries1.s_color='rgba(132,175,189,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries1.s_lineWidth=1;
	option.series.push(aSeries1);			

	/* 2번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries2 	= createSeries('관리목표','line',series[1]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries2.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries2.s_sizeDefault = 3;//점의 크기
	aSeries2.s_shape='circle';//점의 모양
	aSeries2.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries2.s_color='rgba(231,158,228,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries2.s_lineWidth=2;
	aSeries2.s_unit='%';
	aSeries2.s_max = aSeries1.s_max;
	aSeries2.s_min = aSeries1.s_min;
	option.series.push(aSeries2);

	chart.redraw(option);
};
// 수정대체원가그래프(교량)
scwin.fnSelectBrdgHdqrAsetWrthPrssListChart = function(){
	var wsAdapter = {};
	wsAdapter['ref'] = "data:dltBrdgHdqrAsetWrthPrssList";
	wsAdapter['seriesColumns'] = "[['awevAmt','mgmtGoal']]";
	wsAdapter['labelNode'] = "clss";
	var series = getWebSquareSeries(wsAdapter);

	var chartId = 'brdgHdqrAsetWrthPrssListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '원';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(본부)';
	option.unitLabel_Verti			= '금액(억원)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('수정대체원가','bar',series1);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries1.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries1.s_sizeDefault = 1;//점의 크기
	aSeries1.s_shape='circle';//점의 모양
	aSeries1.s_color='rgba(132,175,189,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries1.s_lineWidth=1;
	option.series.push(aSeries1);			

	/* 2번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries2 	= createSeries('관리목표','line',series2);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries2.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries2.s_sizeDefault = 3;//점의 크기
	aSeries2.s_shape='circle';//점의 모양
	aSeries2.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries2.s_color='rgba(231,158,228,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries2.s_lineWidth=2;
	aSeries2.s_unit='%';
	aSeries2.s_max = aSeries1.s_max;
	aSeries2.s_min = aSeries1.s_min;
	option.series.push(aSeries2);

	chart.redraw(option);
};
// 수정대체원가그래프(터널)
scwin.fnSelectTunlHdqrAsetWrthPrssListChart = function(){
	var jsonArr = dltTunlHdqrAsetWrthPrssList.getAllJSON();
	var series1 = [];
	var series2 = [];
	for(var i=0;i < jsonArr.length; i++){
		var aJsonRowSeries = jsonArr[i];
		var aArr = [aJsonRowSeries['clss'], aJsonRowSeries['awevAmt']];
		series1.push(aArr);
		var aArr2 = [aJsonRowSeries['clss'], aJsonRowSeries['mgmtGoal']];
		series2.push(aArr2);
	}

	var chartId = 'tunlHdqrAsetWrthPrssListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(본부)';
	option.unitLabel_Verti			= '금액(억원)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('수정대체원가','bar',series1);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries1.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries1.s_sizeDefault = 1;//점의 크기
	aSeries1.s_shape='circle';//점의 모양
	aSeries1.s_color='rgba(132,175,189,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries1.s_lineWidth=1;
	option.series.push(aSeries1);			

	/* 2번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries2 	= createSeries('관리목표','line',series2);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries2.s_unit	='억원';//툴팁에 표시될 Y값의단위
	aSeries2.s_sizeDefault = 3;//점의 크기
	aSeries2.s_shape='circle';//점의 모양
	aSeries2.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries2.s_color='rgba(231,158,228,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries2.s_lineWidth=2;
	aSeries2.s_unit='%';
	aSeries2.s_max = aSeries1.s_max;
	aSeries2.s_min = aSeries1.s_min;
	option.series.push(aSeries2);

	chart.redraw(option);
};
// 건전지수(전체)
scwin.fnSelectAllHdqrCtniListChart = function(){
	var wsAdapter = {};
	wsAdapter['ref'] = "data:dltAllHdqrAsetWrthPrssList";
	wsAdapter['seriesColumns'] = "[['ctniRslt','ctniMgmtGoal','ctniAvg']]";
	wsAdapter['labelNode'] = "clss";
	var series = getWebSquareSeries(wsAdapter);

	var chartId = 'allHdqrCtniListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(본부)';
	option.unitLabel_Verti			= '건전지수(%)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('건전지수결과','bar',series[0]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries1.s_unit	='%';//툴팁에 표시될 Y값의단위
	aSeries1.s_sizeDefault = 1;//점의 크기
	aSeries1.s_shape='circle';//점의 모양
	aSeries1.s_color='rgba(132,175,189,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries1.s_lineWidth=1;
	option.series.push(aSeries1);			

	/* 2번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries2 	= createSeries('건전지수관리목표','line',series[1]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries2.s_unit	='%';//툴팁에 표시될 Y값의단위
	aSeries2.s_sizeDefault = 3;//점의 크기
	aSeries2.s_shape='circle';//점의 모양
	aSeries2.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries2.s_color='rgba(231,158,228,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries2.s_lineWidth=2;
	option.series.push(aSeries2);

	/* 3번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries3 	= createSeries('건전지수평균','line',series[2]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries3.s_unit	='%';//툴팁에 표시될 Y값의단위
	aSeries3.s_sizeDefault = 3;//점의 크기
	aSeries3.s_shape='circle';//점의 모양
	aSeries3.s_shapeColor='rgba(0,128,192,1)';//점의색상
	aSeries3.s_color='rgba(255,255,0,1)';//시리즈의 색상,  (0,128,192,1)색은 블루
	aSeries3.s_lineWidth=2;
	option.series.push(aSeries3);

	chart.redraw(option);
};
