	aSeries2.s_sizeDefault = 3;//점의 크기
	aSeries2.s_shape='circle';//점의 모양
	aSeries2.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries2.s_color='rgba(231,158,228,1)';//시리즈의 색상, (0,128,192,1)색은 블루
