	aSeries3.s_sizeDefault = 3;//점의 크기
	aSeries3.s_shape='circle';//점의 모양
	aSeries3.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries3.s_color='rgba(255,255,0,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries3.s_lineWidth=2;
	option.series.push(aSeries3);

	/* 4번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries4 	= createSeries('조치편익','line',series4);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
