// 공용년수별보수비및조치편익(교량) 그래프
scwin.fnSelectBrdgPbusYynuHdqrAadcListChart = function(){
	var jsonArr = dltBrdgPbusYynuHdqrAadcList.getAllJSON();
	var series1 = [];
	var series2 = [];
	var series3 = [];
	var series4 = [];
	var rprmCostSum = 0;
	for(var i=0;i < jsonArr.length; i++){
		var aJsonRowSeries = jsonArr[i];
		var aArr = [aJsonRowSeries['pbusYynu'], aJsonRowSeries['rprmCostYear']];
		series1.push(aArr);
		rprmCostSum += aJsonRowSeries['rprmCostYear'];
		rprmCostSum = Math.round(rprmCostSum*1000)/1000;
		var aArr2 = [aJsonRowSeries['pbusYynu'], rprmCostSum];
		series2.push(aArr2);
		var aArr3 = [aJsonRowSeries['pbusYynu'], aJsonRowSeries['rprmCostAvg']];
		series3.push(aArr3);
		var aArr4 = [aJsonRowSeries['pbusYynu'], aJsonRowSeries['actnAdvntCost']];
		series4.push(aArr4);
	}

	var chartId = 'brdgPbusYynuActnAdvntCostListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '년';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(공용년수)';
	option.unitLabel_Verti			= '금액(억원)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('보수비용년도별','bar',series1);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
