scwin.fnSelectAllHdqrAwevBcrValListChart = function(){

	var wsAdapter = {};
	wsAdapter['ref'] = "data:dltAllHdqrRprmPrssList";
	wsAdapter['seriesColumns'] = "[['awevBcrVal','awevBcrValAvg']]";
	wsAdapter['labelNode'] = "clss";
	var series = getWebSquareSeries(wsAdapter);

	var chartId = 'allHdqrAwevBcrValListChart';
	var chart = createChart(chartId);			
	var option = createOption(chart,chartId);						
	option.xrange_axis				= false;//x축이 범위형축		
	option.xunit					= '';//툴팁에 표시되는 X값의단위
	option.unitLabel_Hori			= '구분(본부)';
	option.unitLabel_Verti			= 'B/C ratio(%)';
	//option.chartTitle				= '[Sales Of Beer & Ice-cream vs Temprature Los Angeles Topanga]';	
	option.crossLineShow_hori		= false;//실시간선택라인 수평선 사용안함

	/* 1번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries1 	= createSeries('B/C ratio','bar',series[0]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries1.s_unit	='%';//툴팁에 표시될 Y값의단위
	aSeries1.s_sizeDefault = 1;//점의 크기
	aSeries1.s_shape='circle';//점의 모양
	aSeries1.s_color='rgba(132,175,189,1)';//시리즈의 색상, (0,128,192,1)색은 블루
	aSeries1.s_lineWidth=1;
	option.series.push(aSeries1);			

	/* 2번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries2 	= createSeries('B/C ratio평균','line',series[1]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
