	/* 3번 시리즈 [주의 : createSeries사용시 이름이 반드시 달라야함!!!]*/
	var aSeries3 	= createSeries('저하','line',series[2]);//3번,4번argu->max=null,min=null,divNum=null주면 동적으로 구함
	aSeries3.s_unit	='%';//툴팁에 표시될 Y값의단위
