	aSeries3.s_shape='circle';//점의 모양
	aSeries3.s_shapeColor='rgba(255,255,255,1)';//점의색상
	aSeries3.s_color='rgba(0,128,192,1)';//시리즈의 색상, (0,128,192,1)색은 블루
