scwin.fnSelectAllHdqrAsetWrthPrssList = function(){
	dmaAllSrcParam.reset();
	dmaAllSrcParam.set("strcClssCd" , "00");
	dmaAllSrcParam.set("awevYear" , dateLib.getDate().substring(0,4));
	var id = "fnSelectAllHdqrAsetWrthPrssList";
	var action =  "/awp/selectAllHdqrAsetWrthPrssList.do";
	var ref = ["dmaAllSrcParam"];
	var target = ["dltAllHdqrAsetWrthPrssList"];
	comSubmitLib.callSubmission(id, action, ref, target );
};
