/*---------------------------------------------------------------------------------------
  CallBack
---------------------------------------------------------------------------------------*/
scwin.fnCallback = function(resBody, e, id){
	if(id == "fnSelectAllHdqrAsetWrthPrssList"){ //- 본부별자산가치현황(전체) 목록 조회 후
		spAllHdqrAsetWrthPrssTotCnt.setValue(dltAllHdqrAsetWrthPrssList.getRowCount());
		if(dltAllHdqrAsetWrthPrssList != null && dltAllHdqrAsetWrthPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltAllHdqrAsetWrthPrssList.getRowCount(); i ++){
			//	dltAllHdqrAsetWrthPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
			scwin.fnSelectAllHdqrAsetWrthPrssListChart(); // 수정대체원가(전체)그래프
			scwin.fnSelectAllHdqrCtniListChart(); // 건전지수(전체)그래프
		}
		scwin.fnSelectAllPbusYynuCtniList(); //공용년수별건전지수(전체) 조회
		scwin.fnSelectAllHdqrRprmPrssList(); // 본부별 보수효과현황(전체) 조회
	}else if(id == 'fnSelectBrdgHdqrAsetWrthPrssList'){ //- 본부별자산가치현황(교량) 목록 조회 후
		spBrdgHdqrAsetWrthPrssTotCnt.setValue(dltBrdgHdqrAsetWrthPrssList.getRowCount());
		if(dltBrdgHdqrAsetWrthPrssList != null && dltBrdgHdqrAsetWrthPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltBrdgHdqrAsetWrthPrssList.getRowCount(); i ++){
			//	dltBrdgHdqrAsetWrthPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
			scwin.fnSelectBrdgHdqrAsetWrthPrssListChart(); // 수정대체원가(교량)그래프
			scwin.fnSelectBrdgHdqrCtniListChart(); // 건전지수(교량)그래프
		}			
		scwin.fnSelectBrdgPbusYynuCtniList(); //공용년수별건전지수(교량) 조회
		scwin.fnSelectBrdgHdqrRprmPrssList(); // 본부별 보수효과현황(교량) 조회
	}else if(id == 'fnSelectTunlHdqrAsetWrthPrssList'){ //- 본부별자산가치현황(교량) 목록 조회 후
		spTunlHdqrAsetWrthPrssTotCnt.setValue(dltTunlHdqrAsetWrthPrssList.getRowCount());
		if(dltTunlHdqrAsetWrthPrssList != null && dltTunlHdqrAsetWrthPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltTunlHdqrAsetWrthPrssList.getRowCount(); i ++){
			//	dltTunlHdqrAsetWrthPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
			scwin.fnSelectTunlHdqrAsetWrthPrssListChart(); // 수정대체원가(터널)그래프
			scwin.fnSelectTunlHdqrCtniListChart(); // 건전지수(터널)그래프
		}
		scwin.fnSelectTunlPbusYynuCtniList(); //공용년수별건전지수(터널) 조회
		scwin.fnSelectTunlHdqrRprmPrssList(); // 본부별 보수효과현황(터널) 조회
	}else if(id == 'fnSelectAllPbusYynuCtniList'){ //- 공용년수별건전지수(전체) 목록 조회 후
