			scwin.fnSelectAllStatGradChngListChart(); // 상태등급변동(전체) 그래프출력
		}
		scwin.fnSelectAllPbusYynuHdqrAadcList(); //공용년수별보수비및조치편익(전체) 조회
	}else if(id == 'fnSelectBrdgHdqrRprmPrssList'){ //- 본부별보수효과현황(교량) 목록 조회 후
		spBrdgHdqrRprmPrssTotCnt.setValue(dltBrdgHdqrRprmPrssList.getRowCount());
		if(dltBrdgHdqrRprmPrssList != null && dltBrdgHdqrRprmPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltBrdgHdqrRprmPrssList.getRowCount(); i ++){
			//	dltBrdgHdqrRprmPrssList.setCellData( i , "userPasswdConfirm" , null );
