			//	dltAllYearAsetPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
		}
		$("#grdAllYearAsetPrssList_body_table > .gridHeaderTableDefault").after($("#grdAllYearAsetPrssList_gridFooterTDDefault_tbody"));
		$("#grdAllYearAsetPrssList_dataLayer").css("bottom", "0px");				
		scwin.fnSelectAllHdqrAsetIcdcPrssList(); // 본부별자산증감현황(전체)
	}else if(id == 'fnSelectBrdgYearAsetPrssList'){ //- 년도별자산현황(교량) 목록 조회 후
		spBrdgYearAsetPrssTotCnt.setValue(dltBrdgYearAsetPrssList.getRowCount());
		if(dltBrdgYearAsetPrssList != null && dltBrdgYearAsetPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltBrdgYearAsetPrssList.getRowCount(); i ++){
			//	dltBrdgYearAsetPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
		}
		$("#grdBrdgYearAsetPrssList_body_table > .gridHeaderTableDefault").after($("#grdBrdgYearAsetPrssList_gridFooterTDDefault_tbody"));
		$("#grdBrdgYearAsetPrssList_dataLayer").css("bottom", "0px");
		scwin.fnSelectBrdgHdqrAsetIcdcPrssList(); // 본부별자산증감현황(교량)
	}else if(id == 'fnSelectTunlYearAsetPrssList'){ //- 년도별자산현황(터널) 목록 조회 후
