			//	dltTunlYearAsetPrssList.setCellData( i , "userPasswdConfirm" , null );
			}
		}
		$("#grdTunlYearAsetPrssList_body_table > .gridHeaderTableDefault").after($("#grdTunlYearAsetPrssList_gridFooterTDDefault_tbody"));
		$("#grdTunlYearAsetPrssList_dataLayer").css("bottom", "0px");
		scwin.fnSelectTunlHdqrAsetIcdcPrssList(); // 본부별자산증감현황(터널)
	}else if(id == 'fnSelectAllHdqrAsetIcdcPrssList'){ //- 본부별자산증감현황(전체) 목록 조회 후
