		if(dltAllHdqrAsetIcdcPrssList != null && dltAllHdqrAsetIcdcPrssList.getRowCount() > 0) {
		}
	}else if(id == 'fnSelectBrdgHdqrAsetIcdcPrssList'){ //- 본부별자산증감현황(교량) 목록 조회 후
		spBrdgHdqrAsetIcdcPrssTotCnt.setValue(dltBrdgHdqrAsetIcdcPrssList.getRowCount());
		if(dltBrdgHdqrAsetIcdcPrssList != null && dltBrdgHdqrAsetIcdcPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltBrdgHdqrAsetIcdcPrssList.getRowCount(); i ++){
			//	dltBrdgHdqrAsetIcdcPrssList.setCellData( i , "userPasswdConfirm" , null );
