		}
	}else if(id == 'fnSelectTunlHdqrAsetIcdcPrssList'){ //- 본부별자산증감현황(터널) 목록 조회 후
		spTunlHdqrAsetIcdcPrssTotCnt.setValue(dltTunlHdqrAsetIcdcPrssList.getRowCount());
		if(dltTunlHdqrAsetIcdcPrssList != null && dltTunlHdqrAsetIcdcPrssList.getRowCount() > 0) {
			for(var i = 0; i < dltTunlHdqrAsetIcdcPrssList.getRowCount(); i ++){
