// 탭 클릭시
scwin.tabAsetWrthPrss_ontabclick = function(tabID,idx) {
	if(tabID == "tabAsetWrthPrss_tab_tabBrdg"){
		if(brgdTabOnclick){
			scwin.fnSelectBrdgHdqrAsetWrthPrssList();
			brgdTabOnclick = false;
		}
	}else if(tabID == "tabAsetWrthPrss_tab_tabTunl"){
		if(tunlTabOnclick){
			scwin.fnSelectTunlHdqrAsetWrthPrssList();
			tunlTabOnclick = false;
		}
	}

};
